import { useState, useRef, useEffect, useCallback } from 'react';
import axios from 'axios';
import ThinkingSteps from '../components/ThinkingSteps';

const BASE = process.env.REACT_APP_API_URL || 'http://localhost:8080';

// Simulates the agent pipeline steps in real time
function buildSteps(phase, hasImage) {
  const imageSteps = [
    { id: 'analyze',  label: 'Analyzing image with Gemini Vision',
      detail: 'Identifying issue type, severity and road condition' },
    { id: 'locate',   label: 'Geocoding location',
      detail: 'Converting address to coordinates via Maps API' },
    { id: 'ward',     label: 'Finding municipal ward',
      detail: 'Reverse geocoding to get ward and zone' },
    { id: 'search',   label: 'Searching municipal directory',
      detail: 'Looking up responsible officer in Redis' },
    { id: 'cluster',  label: 'Checking nearby complaints',
      detail: 'Scanning 500m radius for similar issues' },
    { id: 'dispatch', label: 'Sending work order email',
      detail: 'Dispatching to municipal department' },
    { id: 'publish',  label: 'Publishing to public feed',
      detail: 'Posting complaint on the portal' },
  ];

  const chatSteps = [
    { id: 'processing', label: 'Processing your query',
      detail: 'Understanding your question' },
    { id: 'search',   label: 'Searching complaint database',
      detail: 'Looking up relevant data' },
    { id: 'analyze',  label: 'Analyzing trends and patterns',
      detail: 'Generating insights' },
    { id: 'respond',  label: 'Preparing response',
      detail: 'Formatting answer for you' },
  ];

  const all = hasImage ? imageSteps : chatSteps;

  return all.map((s, i) => ({
    ...s,
    active: i <= phase,
    status: i < phase  ? 'done'
          : i === phase ? 'running'
          : 'analyzing'
  }));
}

const SUGGESTIONS = [
  'üöß Report a pothole',
  'üíß Water pipe leaking',
  'üóëÔ∏è Garbage not collected',
  'üí° Streetlight not working',
  'üåä Waterlogging issue',
];

export default function ReportPage() {
  const [messages, setMessages] = useState([]);
  const [input,    setInput]    = useState('');
  const [image,    setImage]    = useState(null);
  const [preview,  setPreview]  = useState(null);
  const [loading,  setLoading]  = useState(false);
  const [phase,    setPhase]    = useState(-1);
  const [started,  setStarted]  = useState(false);
  const [hasImage, setHasImage] = useState(false);
  const bottomRef  = useRef(null);
  const fileRef    = useRef(null);
  const timerRef   = useRef(null);

  useEffect(() => {
    bottomRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, phase]);

  // Simulate step-by-step agent progress
  const simulateSteps = useCallback((onDone, isImageSubmission) => {
    let step = 0;
    const maxSteps = isImageSubmission ? 7 : 4;
    setPhase(0);
    timerRef.current = setInterval(() => {
      step++;
      if (step >= maxSteps) {
        clearInterval(timerRef.current);
        setPhase(maxSteps + 1); // all done
        onDone();
      } else {
        setPhase(step);
      }
    }, 1400);
  }, []);

  const handleImage = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleSubmit = async (text) => {
    const msg = text || input.trim();
    if (!msg && !image) return;

    const isImageSubmission = !!image;
    setHasImage(isImageSubmission);
    setStarted(true);
    setLoading(true);
    setPhase(0);

    // Add user message
    setMessages(prev => [...prev, {
      role:    'user',
      text:    msg,
      image:   preview
    }]);
    setInput('');
    setPreview(null);

    // Add thinking message
    const thinkingId = Date.now();
    setMessages(prev => [...prev, {
      role: 'thinking',
      id:   thinkingId
    }]);

    try {
      const apiPromise = image
        ? submitWithImage(msg)
        : submitChatOnly(msg);

      simulateSteps(async () => {
        try {
          const result = await apiPromise;
          setMessages(prev => prev.map(m =>
            m.id === thinkingId
              ? { role: 'assistant', text: result }
              : m
          ));
        } catch (err) {
          const errorMessage = err?.response?.data?.reply
            || err?.response?.data?.message
            || '‚ùå Something went wrong. Please try again.';
          setMessages(prev => prev.map(m =>
            m.id === thinkingId
              ? { role: 'assistant', text: errorMessage }
              : m
          ));
        } finally {
          setLoading(false);
          setPhase(-1);
          setImage(null);
          setHasImage(false);
        }
      }, isImageSubmission);
    } catch (err) {
      setMessages(prev => prev.map(m =>
        m.id === thinkingId
          ? { role: 'assistant', text: '‚ùå Something went wrong. Please try again.' }
          : m
      ));
      setLoading(false);
      setPhase(-1);
      setImage(null);
    }
  };

  const submitWithImage = async (location) => {
    const fd = new FormData();
    fd.append('image',    image);
    fd.append('location', location || 'Chennai');
    const { data } = await axios.post(`${BASE}/complaint`, fd, {
      headers: { 'Content-Type': 'multipart/form-data' }
    });
    return data.message || 'Complaint submitted successfully!';
  };

  const submitChatOnly = async (msg) => {
    const { data } = await axios.post(`${BASE}/chat`, {
      message: msg,
      session_id: 'public_portal_chat'
    });
    return data.reply || data.message || 'Received your message.';
  };

  // Welcome screen
  if (!started) return (
    <div className="max-w-2xl mx-auto px-4 py-16 flex flex-col
                    items-center text-center">
      <div className="w-16 h-16 rounded-2xl bg-gradient-to-br
                      from-blue-600 to-purple-600 flex items-center
                      justify-center text-3xl mb-6 shadow-lg">
        üèôÔ∏è
      </div>
      <h1 className="text-3xl font-bold text-white mb-2">
        Report a Civic Issue
      </h1>
      <p className="text-zinc-400 mb-10 max-w-md">
        Take a photo, share your location ‚Äî CiviqAI will analyze it
        and notify the right department automatically.
      </p>

      {/* Main input */}
      <div className="w-full max-w-xl">
        <InputBar
          input={input}
          setInput={setInput}
          image={image}
          preview={preview}
          fileRef={fileRef}
          handleImage={handleImage}
          onSubmit={handleSubmit}
          loading={loading}
        />
      </div>

      {/* Suggestions */}
      <div className="flex flex-wrap gap-2 justify-center mt-6">
        {SUGGESTIONS.map(s => (
          <button
            key={s}
            onClick={() => handleSubmit(s)}
            className="px-4 py-2 bg-zinc-900 border border-white/10
                       rounded-full text-sm text-zinc-400
                       hover:border-white/30 hover:text-white
                       transition-all"
          >
            {s}
          </button>
        ))}
      </div>
    </div>
  );

  // Chat view
  return (
    <div className="max-w-2xl mx-auto px-4 flex flex-col
                    h-[calc(100vh-65px)]">

      {/* Messages */}
      <div className="flex-1 overflow-y-auto py-6 space-y-8">
        {messages.map((m, i) => (
          <div key={i}>

            {/* User message */}
            {m.role === 'user' && (
              <div className="mb-6">
                {m.image && (
                  <img src={m.image} alt="uploaded"
                       className="w-full max-h-56 object-cover
                                  rounded-2xl mb-3 border border-white/10"/>
                )}
                <h2 className="text-2xl font-bold text-white leading-tight">
                  {m.text}
                </h2>
              </div>
            )}

            {/* Thinking / Processing */}
            {m.role === 'thinking' && phase >= 0 && (
              <div className="bg-zinc-900 border border-white/10
                              rounded-2xl p-5">
                <div className="flex items-center gap-2 mb-4">
                  <div className="w-4 h-4 border-2 border-zinc-600
                                  border-t-blue-400 rounded-full animate-spin"/>
                  <span className="text-sm font-medium text-zinc-400">
                    {hasImage ? 'CiviqAI is processing your complaint...' : 'CiviqAI is thinking...'}
                  </span>
                </div>
                <ThinkingSteps steps={buildSteps(phase, hasImage)} />
              </div>
            )}

            {/* Assistant response */}
            {m.role === 'assistant' && (
              <div className="space-y-3">
                <div className="flex items-center gap-2 mb-2">
                  <div className="w-6 h-6 rounded-full bg-gradient-to-br
                                  from-blue-500 to-purple-600 flex items-center
                                  justify-center text-xs">ü§ñ</div>
                  <span className="text-sm font-semibold text-white">CiviqAI</span>
                </div>
                <div className="prose prose-invert text-zinc-200
                                text-base leading-relaxed max-w-none">
                  <p>{m.text}</p>
                </div>

                {/* Success card */}
                {m.text?.includes('submitted') ||
                 m.text?.includes('sent') ? (
                  <div className="bg-green-500/10 border border-green-500/20
                                  rounded-xl p-4 flex items-start gap-3">
                    <span className="text-2xl">‚úÖ</span>
                    <div>
                      <p className="text-green-400 font-semibold text-sm">
                        Complaint Submitted
                      </p>
                      <p className="text-green-300/70 text-xs mt-0.5">
                        Your complaint is now live on the feed and the
                        department has been notified.
                      </p>
                    </div>
                  </div>
                ) : null}
              </div>
            )}
          </div>
        ))}
        <div ref={bottomRef} />
      </div>

      {/* Sticky input */}
      <div className="pb-4 pt-2 border-t border-white/10">
        <InputBar
          input={input}
          setInput={setInput}
          image={image}
          preview={preview}
          fileRef={fileRef}
          handleImage={handleImage}
          onSubmit={handleSubmit}
          loading={loading}
        />
      </div>
    </div>
  );
}

// ‚îÄ‚îÄ Shared Input Bar ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
function InputBar({ input, setInput, image, preview, fileRef,
                    handleImage, onSubmit, loading }) {

  const handleKey = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      onSubmit(input);
    }
  };

  return (
    <div className="bg-zinc-900 border border-white/10 rounded-2xl
                    overflow-hidden focus-within:border-white/30
                    transition-colors">

      {/* Image preview strip */}
      {preview && (
        <div className="px-4 pt-3">
          <div className="relative inline-block">
            <img src={preview} alt="preview"
                 className="h-20 w-32 object-cover rounded-xl"/>
            <button
              onClick={() => { }}
              className="absolute -top-1 -right-1 w-5 h-5 bg-zinc-700
                         rounded-full flex items-center justify-center
                         text-xs hover:bg-red-600 transition-colors">
              ‚úï
            </button>
          </div>
        </div>
      )}

      {/* Text input */}
      <div className="flex items-end gap-2 px-4 py-3">
        <textarea
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={handleKey}
          placeholder="Describe the issue or share your location..."
          rows={1}
          className="flex-1 bg-transparent text-white text-sm
                     placeholder:text-zinc-600 resize-none focus:outline-none
                     max-h-32 overflow-y-auto"
          style={{ fieldSizing: 'content' }}
        />

        {/* Action buttons */}
        <div className="flex items-center gap-2 flex-shrink-0">

          {/* Attach photo */}
          <button
            onClick={() => fileRef.current.click()}
            className="w-8 h-8 rounded-full flex items-center justify-center
                       text-zinc-500 hover:text-white hover:bg-white/10
                       transition-colors"
            title="Attach photo"
          >
            <svg className="w-5 h-5" fill="none" stroke="currentColor"
                 viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round"
                    strokeWidth={1.5}
                    d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13"/>
            </svg>
          </button>
          <input ref={fileRef} type="file" accept="image/*"
                 className="hidden" onChange={handleImage} />

          {/* Submit */}
          <button
            onClick={() => onSubmit(input)}
            disabled={loading || (!input.trim() && !image)}
            className="w-8 h-8 rounded-full bg-white flex items-center
                       justify-center disabled:opacity-30 disabled:cursor-not-allowed
                       hover:bg-zinc-200 transition-colors"
          >
            {loading
              ? <div className="w-4 h-4 border-2 border-zinc-400
                                border-t-zinc-800 rounded-full animate-spin"/>
              : <svg className="w-4 h-4 text-black" fill="none"
                     stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round"
                        strokeWidth={2} d="M5 12h14M12 5l7 7-7 7"/>
                </svg>
            }
          </button>
        </div>
      </div>
    </div>
  );
}
