import { useState, useCallback } from 'react';
import MapPanel       from './components/MapPanel';
import ComplaintFeed  from './components/ComplaintFeed';
import SubmitForm     from './components/SubmitForm';
import { useComplaints } from './hooks/useComplaints';
import { useSSE }        from './hooks/useSSE';

const BASE = process.env.REACT_APP_API_URL || 'http://localhost:8080';

export default function App() {
  const {
    complaints, loading, filter,
    setFilter, addComplaint, updateComplaint
  } = useComplaints();

  const [selected, setSelected] = useState(null);
  const [showForm, setShowForm] = useState(false);

  // Live SSE updates
  const handleSSE = useCallback((event) => {
    if (event.type === 'new_pin') {
      addComplaint({
        id:          event.complaint_id,
        issue_type:  event.issue_type,
        lat:         event.lat,
        lng:         event.lng,
        status:      event.status || 'open',
        priority:    event.priority || 'P3',
        location:    event.location || '',
        submitted_at: event.timestamp
      });
    }
    if (event.type === 'pin_update') {
      updateComplaint(event.complaint_id, {
        status:   event.status,
        priority: event.priority
      });
    }
  }, [addComplaint, updateComplaint]);

  useSSE(handleSSE);

  return (
    <div className="min-h-screen bg-slate-100">

      {/* Header */}
      <header className="bg-white border-b border-slate-200 px-6 py-4
                         flex justify-between items-center sticky top-0 z-10">
        <div className="flex items-center gap-3">
          <span className="text-2xl">üèôÔ∏è</span>
          <div>
            <h1 className="font-bold text-lg text-slate-800">CiviqAI</h1>
            <p className="text-xs text-slate-500">
              Chennai Civic Complaints Portal
            </p>
          </div>
        </div>
        <button
          onClick={() => setShowForm(s => !s)}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg
                     font-medium text-sm hover:bg-blue-700 transition-colors"
        >
          {showForm ? '‚úï Close' : '+ Report Issue'}
        </button>
      </header>

      {/* Main layout */}
      <div className="flex h-[calc(100vh-65px)]">

        {/* Left sidebar */}
        <div className="w-96 bg-white border-r border-slate-200
                        overflow-y-auto p-4 flex flex-col gap-4">
          {showForm && (
            <SubmitForm onSuccess={() => setShowForm(false)} />
          )}
          <ComplaintFeed
            complaints={complaints}
            loading={loading}
            filter={filter}
            setFilter={setFilter}
            onCardClick={setSelected}
          />
        </div>

        {/* Map */}
        <div className="flex-1 p-4">
          <MapPanel
            complaints={complaints}
            onPinClick={setSelected}
          />
        </div>

        {/* Detail panel */}
        {selected && (
          <div className="w-80 bg-white border-l border-slate-200 p-4 overflow-y-auto">
            <div className="flex justify-between mb-3">
              <h3 className="font-bold text-slate-800">Complaint Detail</h3>
              <button onClick={() => setSelected(null)}
                      className="text-slate-400 hover:text-slate-600">‚úï</button>
            </div>
            <div className="space-y-2 text-sm">
              <p><b>ID:</b> {selected.id}</p>
              <p><b>Type:</b> {selected.issue_type?.replace(/_/g,' ')}</p>
              <p><b>Location:</b> {selected.location}</p>
              <p><b>Status:</b> {selected.status}</p>
              <p><b>Priority:</b> {selected.priority}</p>
              <p><b>Department:</b> {selected.department || 'Assigning...'}</p>
              <p><b>Officer:</b> {selected.officer_name || 'Pending'}</p>
              {selected.image_url && (
                <img src={`${BASE}/${selected.image_url}`}
                     className="w-full rounded-lg mt-2" alt="complaint" />
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
