import { useState, useRef } from 'react';
import axios from 'axios';

const BASE = process.env.REACT_APP_API_URL || 'http://localhost:8080';

export default function SubmitForm({ onSuccess }) {
  const [form, setForm]       = useState({
    location: '', citizen_email: ''
  });
  const [image,    setImage]   = useState(null);
  const [preview,  setPreview] = useState(null);
  const [loading,  setLoading] = useState(false);
  const [success,  setSuccess] = useState(false);
  const [error,    setError]   = useState('');
  const [locating, setLocating]= useState(false);
  const fileRef = useRef();

  const handleImage = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleGPS = () => {
    setLocating(true);
    navigator.geolocation.getCurrentPosition(
      async (pos) => {
        const { latitude: lat, longitude: lng } = pos.coords;
        try {
          const res = await axios.get(
            `https://maps.googleapis.com/maps/api/geocode/json`,
            { params: {
                latlng: `${lat},${lng}`,
                key: process.env.REACT_APP_MAPS_KEY
            }}
          );
          const addr = res.data.results[0]?.formatted_address || `${lat},${lng}`;
          setForm(f => ({ ...f, location: addr }));
        } catch {
          setForm(f => ({ ...f, location: `${lat},${lng}` }));
        }
        setLocating(false);
      },
      () => { setLocating(false); setError('GPS unavailable. Type location.'); }
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) { setError('Please attach a photo'); return; }
    if (!form.location) { setError('Please enter location'); return; }

    setLoading(true);
    setError('');
    try {
      const fd = new FormData();
      fd.append('image',        image);
      fd.append('location',     form.location);
      fd.append('citizen_email', form.citizen_email);

      await axios.post(`${BASE}/complaint`, fd, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setSuccess(true);
      setImage(null);
      setPreview(null);
      setForm({ location: '', citizen_email: '' });
      setTimeout(() => {
        setSuccess(false);
        onSuccess?.();
      }, 3000);
    } catch (err) {
      setError(err.response?.data?.detail || 'Submission failed. Try again.');
    } finally {
      setLoading(false);
    }
  };

  if (success) return (
    <div className="bg-green-50 border border-green-200 rounded-xl
                    p-6 text-center">
      <p className="text-4xl mb-2">‚úÖ</p>
      <p className="font-semibold text-green-700">Complaint Submitted!</p>
      <p className="text-sm text-green-600 mt-1">
        We've notified the relevant department. You'll receive updates by email.
      </p>
    </div>
  );

  return (
    <form onSubmit={handleSubmit}
          className="bg-white rounded-xl border border-slate-200 p-5 space-y-4">
      <h2 className="font-bold text-lg text-slate-800">
        üì¢ Report a Civic Issue
      </h2>

      {/* Image Upload */}
      <div>
        <label className="block text-sm font-medium text-slate-700 mb-1">
          Photo <span className="text-red-500">*</span>
        </label>
        <div
          onClick={() => fileRef.current.click()}
          className="border-2 border-dashed border-slate-300 rounded-lg
                     p-4 text-center cursor-pointer hover:border-blue-400
                     hover:bg-blue-50 transition-colors"
        >
          {preview
            ? <img src={preview} alt="preview"
                   className="w-full h-40 object-cover rounded" />
            : <div className="text-slate-400">
                <p className="text-3xl">üì∑</p>
                <p className="text-sm mt-1">Click to attach photo</p>
              </div>
          }
        </div>
        <input ref={fileRef} type="file"
               accept="image/*" className="hidden"
               onChange={handleImage} />
      </div>

      {/* Location */}
      <div>
        <label className="block text-sm font-medium text-slate-700 mb-1">
          Location <span className="text-red-500">*</span>
        </label>
        <div className="flex gap-2">
          <input
            type="text"
            placeholder="e.g. Perungudi, OMR Chennai"
            value={form.location}
            onChange={e => setForm(f => ({ ...f, location: e.target.value }))}
            className="flex-1 border border-slate-300 rounded-lg px-3 py-2
                       text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          <button
            type="button"
            onClick={handleGPS}
            disabled={locating}
            className="px-3 py-2 bg-blue-100 text-blue-700 rounded-lg
                       text-sm font-medium hover:bg-blue-200 transition-colors
                       disabled:opacity-50"
          >
            {locating ? '...' : 'üìç GPS'}
          </button>
        </div>
      </div>

      {/* Email */}
      <div>
        <label className="block text-sm font-medium text-slate-700 mb-1">
          Your Email (for updates)
        </label>
        <input
          type="email"
          placeholder="youremail@gmail.com"
          value={form.citizen_email}
          onChange={e => setForm(f => ({ ...f, citizen_email: e.target.value }))}
          className="w-full border border-slate-300 rounded-lg px-3 py-2
                     text-sm focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
      </div>

      {error && (
        <p className="text-red-500 text-sm bg-red-50 px-3 py-2 rounded-lg">
          {error}
        </p>
      )}

      <button
        type="submit"
        disabled={loading}
        className="w-full py-3 bg-blue-600 text-white rounded-lg
                   font-semibold hover:bg-blue-700 transition-colors
                   disabled:opacity-50 disabled:cursor-not-allowed"
      >
        {loading ? '‚è≥ Processing...' : 'üöÄ Submit Complaint'}
      </button>
    </form>
  );
}
